package Herencia;

public class Cuadrado{

	public Cuadrado() {
		// TODO Auto-generated constructor stub
		float lado;
		
	}
	
	public float areaC(float lado){
		float area;
		area = lado*lado;
        return area;
    }
	
	public float perimetroC(float lado){
		float peri;
		peri = lado*4;
        return peri;
    }

}
